import { useState } from 'react'

const AddTask = ({ onAdd }) => {
  const [text, setText] = useState('')
  const [mail, setMail] = useState('')
  const [reminder, setReminder] = useState(false)

  const onSubmit = (e) => {
    e.preventDefault()

    if (!text) {
      alert('Per favore, aggiungi il Titolo della Scadenza!')
      return
    }

    onAdd({ text, mail, reminder })

    setText('')
    setMail('')
    setReminder(false)
  }

  return (
    <form className='add-form' onSubmit={onSubmit}>
      <div className='form-control'>
        <label>Titolo Scadenza</label>
        <input
          type='text'
          placeholder='Scadenza'
          value={text}
          onChange={(e) => setText(e.target.value)}
        />
      </div>
      <div className='form-control'>
        <label>Data & Ora</label>
        <input
          type='date'
          placeholder='Data'
          value={mail}
          onChange={(e) => setMail(e.target.value)}
        />
      </div>
      <div className='form-control form-control-check hide'>
        <label>Completato</label>
        <input
          type='checkbox'
          checked={reminder}
          value={reminder}
          onChange={(e) => setReminder(e.currentTarget.checked)}
        />
      </div>

      <input type='submit' value='AGGIUNGI' className='btn btn-block btn-green' />
    </form>
  )
}

export default AddTask
